const Vendor0007 = require("../models/Vendor0007");

// Get all vendors
const getAllVendors0007 = async(req, res) => {
    try {
        const vendors = await Vendor0007.find().populate("eventId", "name date venue");
        res.status(200).json({ vendors });
    } catch (err) {
        console.error("Error fetching vendors:", err);
        res.status(500).json({ message: "Internal server error" });
    }
};

// Add a new vendor
const addVendor0007 = async(req, res) => {
    const { name, serviceType, contactInfo, eventId, schedule } = req.body;

    let vendor;
    try {
        vendor = new Vendor0007({ name, serviceType, contactInfo, eventId, schedule });
        await vendor.save();
        res.status(201).json({ vendor });
    } catch (err) {
        console.error("Error adding vendor:", err);
        res.status(500).json({ message: "Failed to add vendor" });
    }
};

// Get vendors by event ID
const getVendorsByEvent0007 = async(req, res) => {
    const { eventId } = req.params;

    try {
        const vendors = await Vendor0007.find({ eventId }).populate("eventId", "name date venue");
        res.status(200).json({ vendors });
    } catch (err) {
        console.error("Error fetching vendors for event:", err);
        res.status(500).json({ message: "Internal server error" });
    }
};

// Delete a vendor
const deleteVendor0007 = async(req, res) => {
    const { id } = req.params;

    try {
        const vendor = await Vendor0007.findByIdAndDelete(id);
        if (!vendor) {
            return res.status(404).json({ message: "Vendor not found" });
        }
        res.status(200).json({ message: "Vendor deleted successfully" });
    } catch (err) {
        console.error("Error deleting vendor:", err);
        res.status(500).json({ message: "Failed to delete vendor" });
    }
};

module.exports = {
    getAllVendors0007,
    addVendor0007,
    getVendorsByEvent0007,
    deleteVendor0007,
};